import React, { Component } from "react";
import Card from "./Card";
import Footer from "./Footer";
import api from "./api";
import "./index.css";
import { Button } from "react-bootstrap";
import axios from "axios";

let hoje = new Date();
let horaAtual = hoje.getHours();
let saudacoes = "";

if (horaAtual >= 6 && horaAtual < 12) {
  saudacoes = "Bom dia";
} else if (horaAtual >= 12 && horaAtual < 18) {
  saudacoes = "Boa tarde";
} else {
  saudacoes = "Boa noite";
}

function criarCard(hospedagens: any) {
  return (
    <Card
      key={hospedagens.id}
      nome={hospedagens.titulo}
      descricao={hospedagens.descricao}
      preco={hospedagens.diaria}
      imagem={hospedagens.imagem}
      cidade={hospedagens.cidade}
      estado={hospedagens.estado}
    />
  );
}

export default class MenorPreco extends Component {
  state = {
    hospedagensMenorPreco: [],
  };

  async componentDidMount() {
    const api = axios.create({
      baseURL: "http://localhost:4000/casas",
    })
      .get("/menorpreco")
      .then((response) => {
        this.setState({
          hospedagensMenorPreco: response.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <h1>{saudacoes}, seja bem vindo ao Airbnb!</h1>
          </div>
        </div>
        <div className="row">
          {this.state.hospedagensMenorPreco.map((hospedagens) =>
            criarCard(hospedagens)
          )}
        </div>
        <Footer />
      </div>
    );
  }
}
