import React, { Component } from "react";
import Card from "./Card";
import Footer from "./Footer";
import api from "./api";
import "./index.css";
import { Button } from "react-bootstrap";
import axios from "axios";

let hoje = new Date();
let horaAtual = hoje.getHours();
let saudacoes = "";

if (horaAtual >= 6 && horaAtual < 12) {
  saudacoes = "Bom dia";
} else if (horaAtual >= 12 && horaAtual < 18) {
  saudacoes = "Boa tarde";
} else {
  saudacoes = "Boa noite";
}

function criarCard(hospedagens: any) {
  return (
    <Card
      key={hospedagens.id}
      nome={hospedagens.titulo}
      descricao={hospedagens.descricao}
      preco={hospedagens.diaria}
      imagem={hospedagens.imagem}
      cidade={hospedagens.cidade}
      estado={hospedagens.estado}
    />
  );
}

export default class MenorPreco extends Component {
  state = {
    hospedagens: [],
  };

  async componentDidMount() {
    const response = await api.get("/ordenarPorMenorPreco");
    console.log(response.data);

    this.setState({ hospedagensMenorPreco: response.data });
  }

  render() {
    return (
      <div>
        <h1 className="saudacoes">{saudacoes}, aqui estão as hospedagens:</h1>
        <div className="saudacoes">
          <Button variant="primary">Menor preço</Button>{" "}
          <Button variant="primary">Maior preço</Button>{" "}
        </div>
        <dl className="moradias">{this.state.hospedagensMenorPreco.map(criarCard)}</dl>
        {/* <dl className="moradias">{this.state.hospedagens.map(criarCard)}</dl> */}
        <Footer />
      </div>
    );
  }
}
